
$( document ).ready(function() {
    $(".pf").hide();
    $("#resp_s").click(function(){
        if ($(this).prop('checked')){
            $(".pf").show();
        } else {
            $(".pf").hide();
            }
    });
    $("#resp_n").click(function(){
        if ($(this).prop('checked')){
            $(".pf").hide();
            } else {
            $(".pf").show();
            }
    });
});